<!DOCTYPE html><html lang="en"><head>    <title>Add Employe</title>    <meta charset="utf-8">    <meta name="viewport" content="width=device-width, initial-scale=1">    <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css">    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.3.1/jquery.min.js"></script>    <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js"></script>    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js"></script></head><body><div class="container">    <h2>Employe Info </h2>    <p>Add some Require field Carefully</p>    <form action="{{route('store')}}" class="needs-validation" novalidate method="POST">        @csrf        <div class="form-group">            <label for="uname">Name</label>            <input type="text" class="form-control" id="uname" placeholder="Enter name" name="name" required>            <div class="valid-feedback">Valid.</div>            <div class="invalid-feedback">Please fill out this field.</div>        </div>        <div class="form-group">            <label for="uname">Desigination</label>            <input type="text" class="form-control" id="uname" placeholder="Enter Desigination" name="deg" required>            <div class="valid-feedback">Valid.</div>            <div class="invalid-feedback">Please fill out this field.</div>        </div>        <div class="form-group">            <label for="uname">Salary</label>            <input type="text" class="form-control" id="uname" placeholder="Enter Salary" name="salary" required>            <div class="valid-feedback">Valid.</div>            <div class="invalid-feedback">Please fill out this field.</div>        </div>        <div class="form-group form-check">            <label class="form-check-label">                <input class="form-check-input" type="checkbox" name="remember" required> I agree on blabla.                <div class="valid-feedback">Valid.</div>                <div class="invalid-feedback">Check this checkbox to continue.</div>            </label>        </div>        <button type="submit" class="btn btn-primary">Submit</button>    </form>    <button type="submit" class="btn btn-danger"><a href="{{route('home')}}">Back </a> </button></div><script>    // Disable form submissions if there are invalid fields    (function() {        'use strict';        window.addEventListener('load', function() {            // Get the forms we want to add validation styles to            var forms = document.getElementsByClassName('needs-validation');            // Loop over them and prevent submission            var validation = Array.prototype.filter.call(forms, function(form) {                form.addEventListener('submit', function(event) {                    if (form.checkValidity() === false) {                        event.preventDefault();                        event.stopPropagation();                    }                    form.classList.add('was-validated');                }, false);            });        }, false);    })();</script></body></html>